    private void B4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B4ActionPerformed
        for (int i = pos; i < cont; i++) {
            c[i] = c[i + 1];
        }
        cont--;

        if (cont < pos) {
            pos = cont;
        }

        jTextField1.setText("" + c[pos].getCodigo());
        jTextField2.setText(c[pos].getNome());
        jFormattedTextField1.setText(c[pos].getCPF());
        jFormattedTextField2.setText(c[pos].getFone());
        jRadioButton2.setSelected(c[pos].getSexo() == 'F');
        jRadioButton3.setSelected(c[pos].getSexo() == 'M');



        // TODO add your handling code here:
    }//GEN-LAST:event_B4ActionPerformed

