    private void B2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_B2ActionPerformed
        c[cont] = new Contato();
        c[cont].setCodigo(Integer.parseInt(jTextField1.getText()));
        c[cont].setNome(jTextField2.getText());
        c[cont].setCPF(jFormattedTextField1.getText());
        c[cont].setFone(jFormattedTextField2.getText());
        if (jRadioButton2.isSelected()) {
            c[cont].setSexo('F');
        } else {
            c[cont].setSexo('M');
        }
        cont++;


        // TODO add your handling code here:
    }//GEN-LAST:event_B2ActionPerformed

